import React, { useEffect, useState } from "react";
import styled from "styled-components";

function Noodle() {
  const [noodles, setNoodle] = useState([]);

  useEffect(() => {
    noodle();
  }, []);

  const noodle = async () => {
    const db = await fetch("http://127.0.0.1:8000/foods", {
      credentials: "include", // ここを追加。
    });

    const data = await db.json();
    setNoodle(data);
  };
  return (
    <div>
      <Wrapper>
        <h3>Noodle</h3>

        {noodles.map((noodle) => {
          return (
            <Card>
              <p>{noodle.name}</p>
              <img src={noodle.img} alt={noodle.img} />
            </Card>
          );
        })}
      </Wrapper>
    </div>
  );
}

const Wrapper = styled.div`
  margin: 4rem 0rem;
`;

const Card = styled.div`
  min-height: 25rem;
  border-radius: 2rem;
  overflow: hidden;

  img {
    border-radius: 2rem;
    position: absolute;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

export default Noodle;
