import React, { useEffect, useState } from "react";

function Noodle() {
  const [noodle, setNoodle] = useState([]);

  useEffect(() => {
    foods();
  }, []);

  const foods = async () => {
    const test = await fetch("http://127.0.0.1:8000/foods", {
      credentials: "include", // ここを追加。
    });

    const data = await test.json();
    setNoodle(data);
  };
  return (
    <div>
      {noodle.map((recipe) => {
        return <p>{recipe.name}</p>;
      })}
    </div>
  );
}

export default Noodle;
