import React, { useEffect, useState } from "react";

function Noodle() {
  const [noodle, setNoodle] = useState([]);

  useEffect(() => {
    foods();
  }, []);

  const foods = async () => {
    const test = await fetch("http://127.0.0.1:8000/foods", {
      credentials: "include", // ここを追加。
    });

    const data = await test.json();
    setNoodle(data);
  };
  return (
    <div>
      {noodle.map((recipe) => {
        return (
          <SplideSlide key={recipe.id}>
            {" "}
            <Card>
              <Link to={"/recipe/" + recipe.id}>
                <p>{recipe.name}</p>

                <img src={recipe.img} alt={recipe.img} />
                <Gradient />
              </Link>
            </Card>
          </SplideSlide>
        );
      })}
    </div>
  );
}

export default Noodle;
